<?phpsession_start();error_reporting( E_ALL ^ E_NOTICE );//SETEO LA MEMORIA MINIMA REQUERIRA@ini_set("memory_limit","64M");require_once("../../Connections/conect.php");if($_GET['img']){	//selecciono el tipo de archivo	switch($_GET['t'])		{	case 1;		$campos = "gene_";	break;	case 2;		$campos = "loca_";	break;	case 3;		$campos = "prec_";	break;	case 4;		$campos = "ento_";	break;	case 5;		$campos = "serv_";	break;		}	//preparo el preview	$img = substr($_GET['img'],5);	$tipo = $_FILES[$campos.$img]["type"];	$contenido = $_FILES[$campos.$img]["tmp_name"];	require("class_imagen.inc.php");	$obj = new imagen($contenido,$tipo);	$_SESSION['tmpimg'.$img] = $obj->tratar(90,62);	$_SESSION['tmptipo'.$img] = $tipo;	$contenido = "preimg.php?img=".$_GET['img'];}else{	$sql = "SELECT aga_imagen FROM apartamento_galerias WHERE aga_id='".$_GET['foto']."' limit 0,1;";	$imagen = mysql_query($sql,$mysql->get_conect());		$contenido = "../../dibujar.php?file=archivos/".mysql_result($imagen,0)."&ancho=97&alto=62";}?><html><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8"><style type="text/css" >body{	margin:0px;	padding:0px;	height:62px;	width:90px;	background: url(<?php echo $contenido?>) top no-repeat transparent;}</style></head><body></body></html>